var app = require('../../server/server.js');
var Orders = app.models.orders;


module.exports = {
	save_order: function(order, callback){
		Orders.create(order, function(err, obj){
			if(err){console.log(err)}
				console.log('Success!');
			callback(obj);
		});
	},
	ship_order: function(order, callback){
		Orders.upsert(order, function(err, obj){
			if(err){console.log(err)}
				console.log('Success!');
				callback(obj);
		});
	}

}